import React, { Component } from 'react'

const audioPlayer= new Audio();
class TrackList extends Component {
    constructor(props){
        super(props);
        this.state={
            audioSrc: "https://p.scdn.co/mp3-preview/06342a329129344dbd4a9c31c60e0cdb25b2093d?cid=bda39c7852de422b8f3f9e50b526f361",
            isPlay : false
        };
    }

    playAudio = () => {
        console.log(this.state.isPlay)
        if(!this.state.isPlay){
            this.setState({
                isPlay: true
            });
            audioPlayer.src = this.state.audioSrc;
            audioPlayer.play();
        }
        else{
            audioPlayer.pause();
            this.setState({
                isPlay: false
            });
        }
    }

    render() {
        const {trackList, pageTitle, isSearch, isErr, errText} = this.props;
        //console.log(isErr)
        //console.log(trackList)
        return (
            <div className="trackList py-1 row">
                <div className="row col-12">
                { isSearch ? <h2>{pageTitle}</h2> : ""}
                </div>
                {
                    !isErr ? 
                    trackList.map((item, index) => {
                        return (
                            <div key={index} className="col-4 albumItem">
                                <div className="albumImage">
                                    <img src={item.album.images[1].url}  />
                                </div>
                                <div className="albumText">
                                    <div className="albumInfo">
                                        <span><b>Albüm Adı:</b> {item.album.name}</span>
                                        <span><b>Albüm Yılı:</b> {item.album.release_date}</span>
                                    </div>
                                    <div className="trackInfo">
                                        <span><b>Şarkı Adı:</b> {item.name}</span>
                                        <span><b>Popülerlik:</b> {item.popularity}</span>
                                        <audio controls>
                                        <source src={item.preview_url} type="audio/mp3" />
                                        </audio>
                                        <div className="playVideo">
                                            <span className="btnPlay" onClick= {this.playAudio()}> 
                                            <i class="fas fa-play"></i> Şarkıyı Dinle...
                                            </span>
                                        </div>
                                    </div>
                                    <div className="btnTrack">
                                        <a href={item.external_urls.spotify} target="_blank">Spotify'da çal</a>
                                    </div>
                                </div>
                            </div>
                    )
                    })
                    : <h2>{errText}</h2>
                }
            </div>
        )
    }
}

export default TrackList;
